    \'phone\' => \'phone\',
    \'email\' => \'email\',
    \'comment\' => \'extended[comment]\',
    \'index\' => \'zip\',
    \'country\' => \'country\',
    \'region\' => \'state\',
    \'city\' => \'city\',
    \'street\' => \'address\',
    \'building\' => \'extended[building]\',
    \'room\' => \'extended[room]\',
);
// Apply custom fields
if (!empty($userFields)) {
    if (!is_array($userFields)) {
        $userFields = json_decode($userFields, true);
    }
    if (is_array($userFields)) {
        $fields = array_merge($fields, $userFields);
    }
}
// Set user fields
